import React, { Component } from 'react';
import '../style/AddTest.css'
import { Auth } from 'aws-amplify'
import { Dropdown } from 'semantic-ui-react'



class AddTest extends Component {
    state = {
        testName:"",
        question:"",
        type:"",
        questionObj: {
            "type": "",
            "langugage": "",
            "questionContent": "",
            "numberOfAvaibleAnswers": "",
            "avaibleAnswers": ""
          },
        questionArray:[],
        errors: {
            question: false,
            type: false,
        },
        types:["Open","Closed"]
    }
    
        messages = {
            type_incorect: 'Question type have not been chosen',
            question_incorect: 'Your question has not any letter',
        }
        
        formValidation = () => {
            let question = false
            let type = false
            let correct = false
            
            if (this.state.type=="W" || this.state.type =="O") {
                type = true
            }
            if (this.state.question.length > 0) {
                question = true
            }
            if (question && type) {
                correct = true
            }
            return ({
                correct, question, type
            })
        }
        handleChange = (e) => {
            const name = e.target.name;
            this.setState({
                [name]: e.target.value
            })
        };
        handlePlusButton = async(e)=>{
            alert("User confirmed");
         
        }
        handleSubmit = async (e) => {
            e.preventDefault()
    
            const validation = this.formValidation()
            console.log(validation)
            if (validation.correct) {
                this.setState({
                    message: 'Send, please wait',
                    errors: {
                        question: false,
                        type: false,
                    }
                })
                try {   
                    
                    await Auth.confirmSignUp(this.state.question, this.state.type);
                    // console.log(vall)
                    alert("User confirmed");
                } catch (e) {
                    alert(e.message);
                }
            }
            else {
                this.setState({
                    errors: {
                        type: !validation.type,
                        question: !validation.question,
                    }
                })
            }
        }
        componentDidUpdate() {
            if (this.state.message !== '') {
                setTimeout(() => this.setState({
                    message: ''
                }), 9000);
            }
        }
   
        Greeting(props) {
            const isLoggedIn = props.isLoggedIn;
            if (isLoggedIn) {
              return <UserGreeting />;
            }
            return <GuestGreeting />;
          }
        render() {
            
            return (
             
                <div className="addQuestion">
                     <label htmlFor="testName">testName:
                    <input type="text" id="testName" name="testName" value={this.state.testName} onChange={this.handleChange} />
                    </label>
                    <label htmlFor="type">Chose question type
                    </label>
                    <Greeting isLoggedIn={false} />,
     <select id ='testType' onChange={()=>{
         this.setState({
             type:document.getElementById('testType').value
         })
        
     }}>
         <option value="W">Open</option>
         <option value ="O">Closed</option>
     </select>

               </div>
            );
        }

 
}
export default AddTest;
