import React, { Component } from 'react';
import '../style/AddTest.css'
import { Auth } from 'aws-amplify'
import { Dropdown } from 'semantic-ui-react'
import { PresignedPost } from 'aws-sdk/clients/s3';



class AddTest extends Component {
    state = {
        testName:"",
        question:"",
        type:"O",
        questionObj: {
            "type": "",
            "langugage": "PL",
            "questionContent": "",
            "numberOfAvaibleAnswers": "",
            "avaibleAnswers": ""
          },
        
            question0:"",
            question1:"",
            question2:"",
            question3:"",
            question4:"",
            question5:"",
        
        errors: {
            question: false,
            type: false,
        },
        numberOfQuestion:4,
        types:["Open","Closed"]
    }
    
        messages = {
            type_incorect: 'Question type have not been chosen',
            question_incorect: 'Your question has not any letter',
        }
        
        formValidation = () => {
            let question = false
            let type = false
            let correct = false
            
            if (this.state.type=="W" || this.state.type =="O") {
                type = true
            }
            if (this.state.question.length > 0) {
                question = true
            }
            if (question && type) {
                correct = true
            }
            return ({
                correct, question, type
            })
        }
        handleChange = (e) => {
            const name = e.target.name;
            this.setState({
                [name]: e.target.value
            })
        };
        handleChangeAnswer=(e)=>{
            const name = e.target.name;
            this.setState({
                [name]:document.getElementById(name)
            })
        }
        handlePlusButton = async(e)=>{
            alert("User confirmed");
         
        }
        handleSubmit = async (e) => {
            e.preventDefault()
    
            const validation = this.formValidation()
            console.log(validation)
            if (validation.correct) {
                this.setState({
                    message: 'Send, please wait',
                    errors: {
                        question: false,
                        type: false,
                    }
                })
                try {   
                    
                    await Auth.confirmSignUp(this.state.question, this.state.type);
                    // console.log(vall)
                    alert("User confirmed");
                } catch (e) {
                    alert(e.message);
                }
            }
            else {
                this.setState({
                    errors: {
                        type: !validation.type,
                        question: !validation.question,
                    }
                })
            }
        }
        componentDidUpdate() {
            if (this.state.message !== '') {
                setTimeout(() => this.setState({
                    message: ''
                }), 9000);
            }
        }
        handleQuestionSubmit(){
            var avaibleAnswers = ""
            if(this.state.type == 'O'){}
            else
            {
                for(var i =0;i<this.state.avaibleAnswers;i++)
                avaibleAnswers+=document.getElementById('question'+i);
                avaibleAnswers+='|';
            }
            avaibleAnswers= avaibleAnswers.slice(0,avaibleAnswers.length-2);
                this.setState({
                    questionObj:{
                        type:this.state.type,
                        numberOfAvaibleAnswers:this.state.numberOfAvaibleAnswers,
                        questionContent:this.state.question,
                        avaibleAnswers:avaibleAnswers
                        
                    }
                })


        }
        render() {
            
            return (
             
                <div className="addQuestion">
                     <label htmlFor="testName">testName:
                    <input type="question" id="question" name="question" value={this.state.question} onChange={this.handleChange} />
                    </label>
                    <label htmlFor="type">Chose question type
                    </label>
                    <select id ='testType' onChange={()=>{
        
        this.setState(
            
            {
            type:document.getElementById('testType').value,
        })
       // this.setState();
    }}>
        <option value='O'>Open</option>
        <option value ='W'>Closed</option>
    </select>
                    <Greeting state={this} />,
                    <button onSubmit={this.handleQuestionSubmit}>submit</button>

               </div>
            );
        }

 
}




function numberOfQuestion(state) {
    const  numbers = [2,3,4,5,6];
    return <div>
            <select  id ='numberOfAvaibleAnswers' onChange={()=>{ state.setState({numberOfAvaibleAnswers:document.getElementById('numberOfAvaibleAnswers').value})}}>
     {numbers.map((number)=>
    <option value={number}>{number}</option>)}
            </select>
    </div>
  }
  
function OpenQuestion(state) {
    return <h1></h1>;
  }
  function ClosedQuestion(state){
    const  numbers = [2,3,4,5,6];
    var select = <select  id ='numberOfAvaibleAnswers' defaultValue ='4' onChange={()=>{ state.state.setState({numberOfAvaibleAnswers:document.getElementById('numberOfAvaibleAnswers').value})}}>
    {numbers.map((number)=>
   <option value={number}>{number}</option>)}
           </select>
 
      return <div>
          {select}
      <div>
            
    </div>
        <InputClosedQuestion state={state} />
        </div>
  }
  function InputClosedQuestion(state){
    var rows = [];
    for (var i = 0; i < state.state.state.state.numberOfAvaibleAnswers; i++) {
       
        rows.push(<li htmlFor={'question'+i}>{'Question nubmer: '+(i+1)}
              <input onChange = {state.state.state.handleChangeAnswer} id= {'answer'+i} type="text"  value=''/>
        </li>);
    }
    
            return <div>
                {rows}
            </div>
  }

function Greeting(state) {

    if ( state.state.state.type == "O") {
      return <OpenQuestion state={state.state} />;
    }

    return <ClosedQuestion state={state.state}/>;

  }
export default AddTest;
