import React, { Component } from 'react';
import '../style/AddTest.css'
import { Auth } from 'aws-amplify'




class QuestionFields extends React.Component {
    render() {
      return (
        <button>Confirm2</button>
 
      );
    }
  }
class AddTest extends Component {
    state = {
        question:"",
        type:"",
        questionObj: {
            "type": "",
            "langugage": "",
            "questionContent": "",
            "numberOfAvaibleAnswers": "",
            "avaibleAnswers": ""
          },
        questionArray:[],
        errors: {
            question: false,
            type: false,
        }
    }
        messages = {
            type_incorect: 'Question type have not been chosen',
            question_incorect: 'Your question has not any letter',
        }
        formValidation = () => {
            let question = false
            let type = false
            let correct = false
            
            if (this.state.type=="W" || this.state.type =="O") {
                type = true
            }
            if (this.state.question.length > 0) {
                question = true
            }
            if (question && type) {
                correct = true
            }
            return ({
                correct, question, type
            })
        }
        handleChange = (e) => {
            const name = e.target.name;
            this.setState({
                [name]: e.target.value
            })
        };
        handlePlusButton = async(e)=>{
            alert("User confirmed");
         
        }
        handleSubmit = async (e) => {
            e.preventDefault()
    
            const validation = this.formValidation()
            console.log(validation)
            if (validation.correct) {
                this.setState({
                    message: 'Send, please wait',
                    errors: {
                        email: false,
                        code: false,
                    }
                })
                try {   
                    
                    await Auth.confirmSignUp(this.state.email, this.state.code);
                    // console.log(vall)
                    alert("User confirmed");
                } catch (e) {
                    alert(e.message);
                }
            }
            else {
                this.setState({
                    errors: {
                        email: !validation.email,
                        code: !validation.code,
                    }
                })
            }
        }
        componentDidUpdate() {
            if (this.state.message !== '') {
                setTimeout(() => this.setState({
                    message: ''
                }), 9000);
            }
        }
       renderQuestionField(){
           return <QuestionFields/>
       }
        render() {
            return (
                <div className="addQuestion">
                    <button><img src='https://cdn4.iconfinder.com/data/icons/ui-3d-01-of-3/100/UI_2-512.png' onClick={alert("pies")} /></button>
                </div>
            );
        }
}
export default AddTest;
