import React, { Component } from 'react';
import '../style/Confirm.css'
import { Auth } from 'aws-amplify'

class Confirm extends Component {
    state = {
        username: "",
        code: "",
        message: '',
        errors: {
            username: false,
            code: false,
        }
    }
        messages = {
            username_incorect: 'email does not contain @ ',
            code_incorect: 'Code has to have only 6 signs',
        }
        formValidation = () => {
            let username = false
            let code = false
            let correct = false
            if (this.state.code.length == 6 ) {
                code = true
            }
            if (this.state.username.length > 5 && this.state.username.indexOf(' ') === -1) {
                username = true
            }
            return ({
                correct, code, username
            })
        }
        handleChange = (e) => {
            const name = e.target.name;
            this.setState({
                [name]: e.target.value
            })
        };

        handleSubmit = async (e) => {
            e.preventDefault()
    
            const validation = this.formValidation()
            console.log(validation)
            if (validation.correct) {
                this.setState({
                    message: 'Send, please wait',
                    errors: {
                        username: false,
                        code: false,
                    }
                })
                try {   
                    
                    await Auth.confirmSignUp(this.state.username, this.state.code);
                    // console.log(vall)
                    alert("User confirmed");
                } catch (e) {
                    alert(e.message);
                }
            }
            else {
                this.setState({
                    errors: {
                        username: !validation.username,
                        code: !validation.code,
                    }
                })
            }
        }
        componentDidUpdate() {
            if (this.state.message !== '') {
                setTimeout(() => this.setState({
                    message: ''
                }), 9000);
            }
        }
        render() {
            return (
    
                <div className="confirm">
                    <form onSubmit={this.handleSubmit}>
                        <label htmlFor="user">Username:
                               <input type="text" id="user" name="username" value={this.state.username} onChange={this.handleChange} />
                            {this.state.errors.username && <span> {this.messages.username_incorect}</span>}
                        </label>
    
                        <label htmlFor="code">Code:
                            <input type="code" name="code" id="code" value={this.state.code} onChange={this.handleChange} />
                            {this.state.errors.code && <span> {this.messages.code_incorect}</span>}
                        </label>
    
                        <button>Confirm</button>
                    </form>
                    {this.state.message && <h3>{this.state.message}</h3>}
    
                </div>
            );
        }
}
export default Confirm;
