import React, { Component } from 'react';
import '../style/AddTest.css'
import { Auth } from 'aws-amplify'




class AddTest extends Component {
    state = {
        testName:"",
        question:"",
        type:"O",
        questionObj: {
            "type": "",
            "langugage": "PL",
            "questionContent": "",
            "numberOfAvaibleAnswers": "",
            "avaibleAnswers": ""
          },
        errors: {
            question: false,
            type: false,
        },
        types:["Open","Closed"],
        questionArray:[]
    }
    
        messages = {
            type_incorect: 'Question type have not been chosen',
            question_incorect: 'Your question has not any letter',
        }
        
        formValidation = () => {
            let question = false
            let type = false
            let correct = false
            
            if (this.state.type=="W" || this.state.type =="O") {
                type = true
            }
            if (this.state.question.length > 0) {
                question = true
            }
            if (question && type) {
                correct = true
            }
            return ({
                correct, question, type
            })
        }
        handleChange = (e) => {
            const name = e.target.id;
            this.setState({
                [name]: e.target.value
            })
        };
        handleChangeAnswer=(e)=>{
            const name = e.target.name;
            this.setState({
                [name]:document.getElementById(name)
            })
        }
        handlePlusButton = async(e)=>{
            alert("User confirmed");
         
        }
        handleSubmit = async (e) => {
            e.preventDefault()
    
            const validation = this.formValidation()
            console.log(validation)
            if (validation.correct) {
                this.setState({
                    message: 'Send, please wait',
                    errors: {
                        question: false,
                        type: false,
                    }
                })
                try {   
                    
                    await Auth.confirmSignUp(this.state.question, this.state.type);
                    // console.log(vall)
                    alert("User confirmed");
                } catch (e) {
                    alert(e.message);
                }
            }
            else {
                this.setState({
                    errors: {
                        type: !validation.type,
                        question: !validation.question,
                    }
                })
            }
        }
        componentDidUpdate() {
            if (this.state.message !== '') {
                setTimeout(() => this.setState({
                    message: ''
                }), 9000);
            }
        }
        handleQuestionSubmit(){
            var avaibleAnswers = ""
            var numberOfAvaibleAnswers= this.state.numberOfAvaibleAnswers;
            if(this.state.type == 'O'){
                    numberOfAvaibleAnswers =0;
            }
            else
            {
                for(var i =0;i<this.state.avaibleAnswers;i++)
                avaibleAnswers+=document.getElementById('question'+i);
                avaibleAnswers+='|';
            }
            avaibleAnswers= avaibleAnswers.substring(0,avaibleAnswers.length-2);
                
                    var questionObj={
                        type:this.state.type,
                        language:"PL",
                        numberOfAvaibleAnswers:numberOfAvaibleAnswers,
                        questionContent:this.state.question,
                        avaibleAnswers:avaibleAnswers
                        
                    }
                    console.log(questionObj)
                
        }
        render() {
            
            return (
             
                <div className="addQuestion">
                     <label htmlFor="testName">testName:
                    <input type="question" id="question" name="question" value={this.state.question} onChange={this.handleChange} />
                    </label>
                    <label htmlFor="type">Chose question type
                    </label>
                    <select id ='testType' onChange={()=>{
        
        this.setState(
            
            {
            type:document.getElementById('testType').value,
        })
       // this.setState();
    }}>
        <option value='O'>Open</option>
        <option value ='W'>Closed</option>
    </select>
                    <QuestionType type={this.state.type} question={this.state.question} />,
               </div>
            );
        }

 
}
